import "firebase/auth";
import "firebase/database";
import { useMemo, useState, useEffect, useCallback } from "react";
import { firebase } from './firebaseTest'; // カスタムフックにしておく

const useDatabase = () => {
  // 同じパスでは毎回同じ結果が得られるのでmemo化しておく
  return useMemo(() => firebase.database().ref("/sample"), []);
}; //   データを取得する
// hooksを使いたいのでカスタムhooksにしておく


const useFetchData = ref => {
  const {
    0: data,
    1: setData
  } = useState();
  useEffect(() => {
    // イベントリスナーを追加するにはonを使う
    ref.on("value", snapshot => {
      // パスに対する全データを含むsnapshotが渡される
      // ない場合はnullが変えるので存在をチェックしておく
      if (snapshot === null || snapshot === void 0 ? void 0 : snapshot.val()) {
        setData(snapshot.val());
      }
    });
    return () => {
      ref.off();
    }; // refの変更に応じて再取得する
    //   指定したパスのデータに対する更新をすべて検知するにはvalueを指定すれば良い。
  }, [ref]); // データを返却する

  return {
    data
  };
}; // 実際に呼び出す際はこちらを使う


export const useFetchAllData = () => {
  // refを取得して
  const ref = useDatabase(); // ref渡してデータを取得する

  return useFetchData(ref);
}; // 登録処理
// この登録はsetに渡した値での登録となる。
// つまり、既存のデータも含めて渡してあげないと登録済みのデータが消える。

const useSetDocument = ref => {
  const updateDocument = useCallback(document => {
    // refについては前回の記事参照
    // setに登録したいデータを渡してあげれば登録できる
    ref.set(document);
  }, [ref]);
  return updateDocument;
};

export const useRegisterData = () => {
  // 前回作ったuseDatabase()を使いref取得
  const ref = useDatabase();
  const setDocument = useSetDocument(ref); // 登録済みのデータを全部取得する

  const {
    data: registeredData
  } = useFetchAllData(); // データを登録する関数を返却する

  const registerData = useCallback(registerData => {
    // 既存のデータと登録するkey-valueを合わせて登録関数に渡す
    setDocument(_objectSpread(_objectSpread({}, registeredData), registerData));
  }, [setDocument, registeredData]); // 返した関数を利用することで、登録済みデータ全て+登録したいデータをDBに保管できる

  return registerData;
}; // 更新処理

const useUpdateDocument = ref => {
  // ref.updateがObjectを受け取るので、Objectを引数に取る関数を定義
  const updateDocument = useCallback(document => ref.update(document), [ref]);
  return updateDocument;
};

export const useUpdateData = () => {
  // setの時と同じくrefを取得して、
  const ref = useDatabase(); // 関数呼び出して

  const updateDocument = useUpdateDocument(ref); // 更新処理を作成する

  const updateData = useCallback(registerData => {
    updateDocument(registerData);
  }, [updateDocument]);
  return updateData;
}; // 削除処理

const useRemoveDocument = ref => {
  // 特に引数が必要ないのでただ呼び出すのみ
  const deleteDocument = useCallback(document => ref.child(document).remove(), [ref]);
  return deleteDocument;
}; // set、updateと同じなので割愛


export const useDelteData = () => {
  const ref = useDatabase();
  const removeDocument = useRemoveDocument(ref);
  const deleteData = useCallback(deleteData => removeDocument(deleteData), [removeDocument]);
  return deleteData;
};
