import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  userId: ''
};
const userModule = createSlice({
  name: "user",
  initialState,
  reducers: {
    login(state, action) {
      state.userId = action.payload;
    }

  }
});
export const {
  login
} = userModule.actions;
export default userModule;
