import React from "react";
import { AppProps } from "next/app";
import { Provider } from "react-redux";
import store from "../modules/store";
import { userTask } from "../modules/userTasksType";

const initialState: userTask = {
    userTaskInfo: {
        calendar: {
            [new Date("Thu, 22 Oct 2020 02:00:00").getFullYear()]: {
                [new Date("Thu, 22 Oct 2020 02:00:00").getMonth() + 1]: {
                    [new Date("Thu, 22 Oct 2020 02:00:00").getDate()]: {
                        PatternId: 1,
                        tasks: [
                            {
                                order: 1,
                                detail: {
                                    ["タスク名"]: "タスク名 22 1",
                                    ["testDetail1"]: "testDetail1 22 1",
                                    ["testDetail2"]: "testDetail1 22 1",
                                },
                                flug: false,
                            },
                            {
                                order: 2,
                                detail: {
                                    ["タスク名"]: "タスク名 22 2",
                                    ["testDetail1"]: "testDetail1 22 2",
                                    ["testDetail2"]: "testDetail1 22 2",
                                },
                                flug: false,
                            },
                        ],
                    },
                    [new Date("Thu, 23 Oct 2020 02:00:00").getDate()]: {
                        PatternId: 1,
                        tasks: [
                            {
                                order: 1,
                                detail: {
                                    ["タスク名"]: "タスク名 23 1",
                                    ["testDetail1"]: "testDetail1 23 1",
                                    ["testDetail2"]: "testDetail1 23 1",
                                },
                                flug: false,
                            },
                            {
                                order: 2,
                                detail: {
                                    ["タスク名"]: "タスク名 23 2",
                                    ["testDetail1"]: "testDetail1 23 2",
                                    ["testDetail2"]: "testDetail1 23 2",
                                },
                                flug: false,
                            },
                        ],
                    },
                },
            },
        },

        pattern: [
            {
                patternId: 1,
                patternName: "testPattern1",
            },
            {
                patternId: 2,
                patternName: "testPattern2",
            },
        ],
        tasks: [
            {
                detail: {
                    ["タスク名"]: "タスク名 22 1",
                    ["defalut"]: "defalut",
                    ["testDetail1"]: "testDetail1 22 1",
                    ["testDetail2"]: "testDetail1 22 1",
                },
                patternInfo: [
                    {
                        patternID: 1,
                        order: 1,
                    },
                ],
            },
            {
                detail: {
                    ["タスク名"]: "タスク名 22 2",
                    ["defalut"]: "defalut",
                    ["testDetail1"]: "testDetail1 22 2",
                    ["testDetail2"]: "testDetail1 22 2",
                },
                patternInfo: [
                    {
                        patternID: 1,
                        order: 2,
                    },
                ],
            },
            {
                detail: {
                    ["タスク名"]: "タスク名 23 1",
                    ["defalut"]: "defalut",
                    ["testDetail1"]: "testDetail1 23 1",
                    ["testDetail2"]: "testDetail1 23 1",
                },
                patternInfo: [
                    {
                        patternID: 2,
                        order: 1,
                    },
                    {
                        patternID: 1,
                        order: 3,
                    },
                ],
            },
            {
                detail: {
                    ["タスク名"]: "タスク名 23 2",
                    ["defalut"]: "defalut",
                    ["testDetail2"]: "testDetail1 23 2",
                    ["testDetail1"]: "testDetail1 23 2",
                },
                patternInfo: [
                    {
                        patternID: 2,
                        order: 2,
                    },
                ],
            },
        ],
    },
};


const HabitPlanApp = ({ Component, pageProps }: AppProps) => {
    const UserTaskContext = React.createContext(initialState);
    return (
        <Provider store={store}>
            <UserTaskContext.Provider value={initialState}>
                <Component {...pageProps} />
            </UserTaskContext.Provider>
        </Provider>
    );
};

export default HabitPlanApp;
