import React, { createContext, useReducer } from "react";
import { AppProps } from "next/app";
import { Provider } from "react-redux";
import store from "../modules/store";
import { userTask } from "../modules/userTasksType";

// const initialState = 'test出力';
type State = {
    count: number
}
     
type Action =
{ type: 'INCREMENT' } |
{ type: 'DECREMENT' }
 
const reducer = (state: State, action: Action) => {
    switch(action.type) {
        case 'INCREMENT':
            return {
                ...state,
                count: state.count + 1
            }
        case 'DECREMENT':
            return {
                ...state,
                count: state.count - 1
            }
        default:
            return state
    }
}
const initialState = {
    count: 0
}
 
const UserTaskContext = createContext({} as {
    state: State
    dispatch: React.Dispatch<Action>
})

// export const UserTaskContext = React.createContext(initialState);

const HabitPlanApp = ({ Component, pageProps }: AppProps) => {
    const [state, dispatch] = useReducer(reducer, initialState)
    return (
        <Provider store={store}>
            <UserTaskContext.Provider value={{ state, dispatch }}>
                <Component {...pageProps} />
            </UserTaskContext.Provider>
        </Provider>
    );
};

export default HabitPlanApp;
