import React from "react";
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import ListSubheader from "@material-ui/core/ListSubheader";
import Switch from "@material-ui/core/Switch";
import WifiIcon from "@material-ui/icons/Wifi";
import BluetoothIcon from "@material-ui/icons/Bluetooth";
import { useSelector } from "react-redux";
import { RootState } from "../../modules/rootReducer";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            width: "100%",
            maxWidth: 360,
            backgroundColor: theme.palette.background.paper,
        },
    })
);

export default function PatternSwitchList() {
    const classes = useStyles();
    const [checked, setChecked] = React.useState(["wifi"]);
    const { userTaskInfo } = useSelector((state: RootState) => state.tasks);

    const handleToggle = (value: string) => () => {
        const currentIndex = checked.indexOf(value);
        const newChecked = [...checked];

        if (currentIndex === -1) {
            newChecked.push(value);
        } else {
            newChecked.splice(currentIndex, 1);
        }

        setChecked(newChecked);
    };

    return (
        <List
            subheader={<ListSubheader>Settings</ListSubheader>}
            className={classes.root}
        >
            {userTaskInfo.pattern.map((content) => {
                <ListItem>
                    <ListItemIcon>
                        <WifiIcon />
                    </ListItemIcon>
                    <ListItemText id={content.patternId + '_' + content.patternName} primary={content.patternName} />
                    <ListItemSecondaryAction>
                        <Switch
                            edge="end"
                            onChange={handleToggle("wifi")}
                            checked={checked.indexOf("wifi") !== -1}
                            inputProps={{
                                "aria-labelledby": "switch-list-label-wifi",
                            }}
                        />
                    </ListItemSecondaryAction>
                </ListItem>;
            })}
            <ListItem>
                <ListItemIcon>
                    <BluetoothIcon />
                </ListItemIcon>
                <ListItemText
                    id="switch-list-label-bluetooth"
                    primary="Bluetooth"
                />
                <ListItemSecondaryAction>
                    <Switch
                        edge="end"
                        onChange={handleToggle("bluetooth")}
                        checked={checked.indexOf("bluetooth") !== -1}
                        inputProps={{
                            "aria-labelledby": "switch-list-label-bluetooth",
                        }}
                    />
                </ListItemSecondaryAction>
            </ListItem>
        </List>
    );
}
