import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";

import TaskList from "../components/view/TaskList";
import ResponsiveDrawer from "../components/view/ResponsiveDrawer";
import FloatingActionButton from "../components/view//FloatingActionButton";

import ListComponent from "../components/test/firebaseTest/getData";
import FormComponent from "../components/test/firebaseTest/FormComponent";
import FirebaseAuthComponent from "../components/test/firebaseTest/FirebaseAuthComponent";
import { useSelector } from "react-redux";
import { useFirebaseConnect, isLoaded, isEmpty, getFirebase } from "react-redux-firebase";

import withAuth from "../components/shared/withAuth";
import { RootState } from "../modules/rootReducer";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        button: {
            backgroundColor: "green",
        },
        taskList: {
            display: "flex",
            paddingTop: "90px",
            paddingLeft: "0px",
            [theme.breakpoints.up("sm")]: {
                paddingLeft: "240px",
            },
        },
    })
);

const Tasks = () => {
    const classes = useStyles();

    // Firebase接続テストここから＝＝＝＝＝＝＝
    console.log("Firebase接続テストここから＝＝＝＝＝＝＝");
    const { userId } = useSelector((state: RootState) => state.user);

    console.log("users/" + userId);
    useFirebaseConnect([
        { path: `users/${userId}` },
        //"users/" + userId { path: '/todos' } // object notation
    ]);
    
    console.log("state.firebase.data.users[userId]");
    const todos = useSelector((state: RootState) => state.firebase.data.users[userId].calendar);
    console.log(todos);
    let testContent;
    if (!isLoaded(todos)) {
        testContent = <div>Loading...</div>;
    }

    if (isEmpty(todos)) {
        testContent = <div>Todos List Is Empty</div>;
    }

    const firebase = getFirebase();
    const testTasks = {
        detail: {
            ["タスク名"]: "======タスク名 22 1",
            ["defalut"]: "======defalut testtest",
            ["testDetail1"]: "======testDetail1 22 1",
            ["testDetail2"]: "======testDetail1 22 1",
        },
        patternInfo: [
            {
                patternID: 1,
                order: 1,
            },
        ],
    }
    console.log('====test====');
    console.log(firebase.push(`users/${userId}/tasks`,testTasks));

    // Firebase接続テストここまで＝＝＝＝＝＝＝
    console.log("Firebase接続テストここまで＝＝＝＝＝＝＝");

    return (
        <Container>
            {/* // <Box> */}
            {/* <Box component="span" m={1} className={classes.button}> */}
            <ResponsiveDrawer />
            {/* <DenseAppBar /> */}
            <Container className={classes.taskList}>
                <FloatingActionButton />
                {/* <TaskList /> */}
            </Container>
            {/* <Tooltip /> */}
            {/* <Tooltips onClick={}/> */}
            {/* <BottomNavigation /> */}
            {/* </Box> */}
            {/* </Box> */}
            <Container className={classes.taskList}>
                {testContent}
                <FirebaseAuthComponent />
                <ListComponent />
                <FormComponent />
            </Container>
        </Container>
    );
};

export default withAuth(Tasks);
